sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	g_form.clearMessages();
   if (isLoading || newValue === '') {
      return;
   }

   // Convert the entered date of birth to a JavaScript Date object
   var dob = new Date(newValue);

   // Get the current date
   var currentDate = new Date();

   // Calculate the difference in years
   var age = currentDate.getFullYear() - dob.getFullYear();

   // Check if the user is 18 years or above and less than 100 years
   if (age >= 18 && age < 100) {
      // Date of birth is valid, you can perform further actions here
      g_form.addInfoMessage('Date of birth is valid.');
   } else {
      // Date of birth is not valid, handle this case accordingly
      g_form.addErrorMessage('Invalid date of birth. Please enter a valid date.');
   }
}
